media.map:
function (doc, meta) {
  if (doc.type === "file" && doc.userdata && doc.userdata.Make) {
    emit(['Picture', doc.userdata.Make, doc.userdata.Model], doc.length);
  } else if (doc.type === "file" && doc.userdata && doc.userdata.Artist && doc.userdata.Album && doc.userdata.Name) {
    emit(['Music', doc.userdata.Artist, doc.userdata.Album, doc.userdata.Name], doc.length);
  }
}

