blobusage.map:
function (doc, meta) {
  if (doc.type === "file") {
    emit([doc.oid, "file", meta.id], null);
  } else if (doc.type === "blob") {
    for (var node in doc.nodes) {
      emit([doc.oid, "blob", node], null);
    }
  }
}

node_blobs.map:
function (doc, meta) {
  if (doc.type === "blob") {
    for (var n in doc.nodes) {
      emit(n, null);
    }
  }
}

node_size.map:
function (doc, meta) {
  if (doc.type === "blob") {
    for (var n in doc.nodes) {
      emit(n, doc.length);
    }
  }
}

